/**
 * AI-Enhanced Code-to-Music Generator
 * Uses OpenAI/Grok to generate better chord progressions, melodies, and musical elements
 */

import OpenAI from 'openai';
import type { ParsedCode } from '../../../shared/types/codeToMusic';

const openaiApiKey = process.env.OPENAI_API_KEY || process.env.AI_INTEGRATIONS_OPENAI_API_KEY;
const xaiApiKey = process.env.XAI_API_KEY;

let openaiClient: OpenAI | null = null;
let grokClient: OpenAI | null = null;

if (openaiApiKey && openaiApiKey.startsWith('sk-')) {
  openaiClient = new OpenAI({ apiKey: openaiApiKey, timeout: 30000 });
}

if (xaiApiKey) {
  grokClient = new OpenAI({
    apiKey: xaiApiKey,
    baseURL: "https://api.x.ai/v1",
    timeout: 30000
  });
}

export interface AIEnhancedMusic {
  chords: Array<{
    chord: string;
    duration: number;
    start: number;
  }>;
  melody: Array<{
    note: string;
    octave: number;
    start: number;
    duration: number;
    velocity: number;
  }>;
  bassline: Array<{
    note: string;
    octave: number;
    start: number;
    duration: number;
  }>;
  suggestions: {
    mood: string;
    energy: string;
    musicalStyle: string;
  };
}

function getClient() {
  if (grokClient) return { client: grokClient, model: "grok-3" };
  if (openaiClient) return { client: openaiClient, model: "gpt-4" };
  return null;
}

export function isAIAvailable(): boolean {
  return getClient() !== null;
}

